import React, { useRef, useState } from "react";
import ArrowBackIosIcon from "@mui/icons-material/ArrowBackIos";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import ListItem from "../listItem/ListItem";
import "./list.scss";

const List = () => {


  const [slideNumber, setSlideNumber] = useState(0)
  const listRef = useRef();

  const handleClick = (direction) => {
    let dist = listRef.current.getBoundingClientRect().x - 35;
    if (direction === 'left' && slideNumber > 0) {
      let updateSlideNum = slideNumber - 1;
      setSlideNumber(slideNumber - 1);
      listRef.current.style.transform = `translate(${225 + dist}px)`
    }

    if (direction === 'right' && slideNumber < 0) {
      setSlideNumber(slideNumber + 1);
      listRef.current.style.transform = `translate(${-225 + dist}px)`
    }

    console.log(slideNumber)


  }


  return (
    <div className="list" style={{ color: "white" }}>
      <span>Netflix Original</span>
      <div className="wrapper">
        <ArrowBackIosIcon className="slider left" onClick={() => handleClick("left")} />
        <div className="container" ref={listRef}>
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
          <ListItem />
        </div>
        <ArrowForwardIosIcon className="slider right" onClick={() => handleClick("right")} />
      </div>
    </div>
  );
};

export default List;
